import { getId, getByPath, clone, trim, isObject } from '@walkeros/core';

export function processData(data) {
  return data.map(item => ({
    ...item,
    id: getId(8),
    timestamp: new Date().toISOString().split('T')[0],
    processed: true
  }));
}

export function extractNestedValues(data, path) {
  return data.map(item => getByPath(item, path, null)).filter(val => val !== null);
}

export function deepCloneData(data) {
  return clone(data);
}

export function cleanStringData(data) {
  return data.map(item => ({
    ...item,
    name: typeof item.name === 'string' ? trim(item.name) : item.name
  }));
}

// Re-export walkerOS utilities
export { getId, getByPath, clone, trim, isObject };
