export function generateSessionId() {
  return `session_${getId(12)}`;
}

export function extractConfigValue(config, path) {
  return getByPath(config, path, 'default');
}

export function createLogEntry(data) {
  return {
    id: getId(6),
    timestamp: new Date().toISOString(),
    message: getByPath(data, 'message', 'No message'),
    level: getByPath(data, 'level', 'info')
  };
}

